class Example {
   static void main(String[] args){
      //Creating a connection to the database
      def sql = Sql.newInstance('jdbc:mysql://awstadb01.cbvy8fxiaste.us-east-1.rds.amazonaws.com:1433/AWSRDSODS01', 'SVC_AWS_ESB_QA', 
         '9IIYNlGWhIObVZSUg9CR', 'com.mysql.jdbc.Driver')

      sql.connection.autoCommit = false
      def sqlstr = "Update OnlineOrdersReconciliation SET OrderStatus='SUBMITTED', PaymentStatus='NOT PAID' where OrderID=36729" 

      try {
         sql.execute(sqlstr);
         sql.commit()
         println("Successfully committed")
      }catch(Exception ex) {
         sql.rollback() 
         println("Transaction rollback")
      }

      sql.close()
   } 
}

